class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        @cache
        def recurse(idx, prev1, prev2):
            if idx == len(nums):
                return 1

            res = 0

            for i in range(nums[idx] + 1):
                val1 = nums[idx] - i
                val2 = i

                if val1 >= prev1 and val2 <= prev2:
                    res += recurse(idx + 1, val1, val2)

            return res

        return recurse(0, float("-inf"), float("inf")) % (10 ** 9 + 7)
