class Solution {
    Long[][] dp;
    int mod = (int)(1e9) + 7;
    public int countOfPairs(int[] nums) {
        int n = nums.length;
        dp = new Long[n][52];
        return (int)func(0, 0, nums);
    }

    private long func(int i, int prev, int[] ar)
    {
        if(i == ar.length)
            return 1;
        if(dp[i][prev] != null)
            return dp[i][prev];
        long res = 0;
        for(int j = 0; j <= ar[i]; j++)
        {
            if(prev != 0)
            {
                if(j < prev - 1)
                    continue;
                if((ar[i] - j) > (ar[i-1] - (prev - 1)))
                    continue;
            }
            res += func(i+1, j + 1, ar);
            res %= mod;
        }
        return dp[i][prev] = res;
    }
}
