class Solution {
    public int countOfPairs(int[] nums) {
        int n = nums.length,mod = 1000000007;
        int[][] dp = new int[n][51];
        for (int x = 0; x <= nums[0]; x++) dp[0][x] = 1;
        for (int i = 1; i < n; i++) {
            for (int x = 0; x <= nums[i]; x++) {
                for (int y = 0; y <= x; y++) {
                    if (nums[i] >= x && nums[i] - x <= nums[i-1] - y) dp[i][x] = (dp[i][x] + dp[i-1][y]) % mod;
                }
            }
        }
        int ans = 0;
        for (int x = 0; x <= nums[n-1]; x++) ans = (ans + dp[n-1][x]) % mod;
        return ans;
    }
}
