class Solution
{
    public:
    const int mod = 1e9 + 7;
    vector<vector<vector< int>>> dp;
    int rec(int i, int prev1, int prev2, vector<int> &nums)
    {
        if (i == nums.size()) return 1;
        if (dp[i][prev1][prev2] != -1) return dp[i][prev1][prev2];

        int res = 0;
        for (int x = prev1; x <= nums[i]; x++)
        {
            int y = nums[i] - x;
            if (y <= prev2)
            {
                res = (res + rec(i + 1, x, y, nums)) % mod;
            }
        }

        dp[i][prev1][prev2] = res;
        return res;
    }

    int countOfPairs(vector<int> &nums)
    {
        int n = nums.size();
        dp.resize(n, vector<vector<int>> (51, vector<int> (51, -1)));
        int ans = rec(0, 0, 50, nums);
        return ans;
    }
};
