class Solution {
public:
    #define ll long long
    ll mod = 1e9+7;
    ll dp[2007][52];
    int n;
    vector<int>v;
    ll DP(int i, int k)
    {
        if (v[i] < k)
            return 0;
        if(i == n-1)
            return 1;
        if(dp[i][k] != -1)
            return dp[i][k];
        ll suma=0;
        ll otro=v[i]-k;
        for (int _=k; _<=50; _++)
        {
            if(v[i+1]-_ > otro) continue;                
            suma+=DP(i+1, _);
            suma%=mod;
        }
        suma%=mod;
        return (dp[i][k] = suma);
    }
    int countOfPairs(vector<int>& nums) 
    {
        v=nums;
        n=v.size();
        for(int i=0; i<=n+1; i++)
            for(int k = 0; k <= 51; k++)
                dp[i][k]=-1;

        ll suma=0;
        for(int k = 0; k <= 50; k++)
        {
            suma+=DP(0, k);
            suma%=mod;
        }
        return suma;
    }
};
