class Solution {

    public long helper(int[] nums,int ind,int last,int max,Long[][] dp){
        if(ind==nums.length){
            return 1;
        }
        if(dp[ind][last]!=null){
            return dp[ind][last];
        }
        if(ind==0){
            long total=0;
            for(int j=last;j<=max;j++){
                int a1=j;
                int a2=nums[ind]-a1;

                if(a2>=0 && a1>=0){
                    long temp=helper(nums,ind+1,j,max,dp);
                    total=(total+temp)%mod;
                }
            }

            dp[ind][last]=total%mod;
            return dp[ind][last];
        }
        else{
            long total=0;
            for(int j=last;j<=max;j++){
                int a1=j;
                int a2=nums[ind]-a1;

                int last1=last;
                int last2=nums[ind-1]-last1;

                if(a1>=last1 && a2<=last2 && a2>=0 && a1>=0){
                    long temp=helper(nums,ind+1,j,max,dp);
                    total=(total+temp)%mod;
                }
            }

            dp[ind][last]=total%mod;
            return dp[ind][last];
        }
    }
    long mod=(long)(1e9)+(long)7;
    public int countOfPairs(int[] nums) {
        int max=nums[0];
        for(int i:nums){
            max=Math.max(max,i);
        }
        return (int)(helper(nums,0,0,max,new Long[nums.length+1][max+1])%mod);
    }
}
