class Solution {
public:
    int mod = 1e9 + 7;
    int dp[2001][51];
    int f(vector<int>& nums, int i, int prev) {
        if (i == nums.size()) return 1;
        if(~dp[i][prev]) return dp[i][prev];
        int ans = 0;
        for (int j = prev; j <= nums[i]; j++) {
            if (nums[i-1] - prev >= nums[i] - j)
                ans = (ans + f(nums, i + 1, j)) % mod;
        }

        return dp[i][prev] =  ans;
    }

    int countOfPairs(vector<int>& nums) {
        int ans = 0;
        memset(dp, -1, sizeof(dp));
        for (int i = 0; i <= nums[0]; i++) { 
            ans = (ans + f(nums, 1, i)) % mod;
        }
        return ans;
    }
};
