class Solution {
public:
    int rec(int i,vector<int> &nums,int l,int r,vector<vector<vector<int>>> &dp1,int m) {
        if(i>=nums.size()) return 1;
        if(dp1[i][l][r]!=-1) return dp1[i][l][r];
        int ans=0;
        for(int j=l;j<=nums[i];j++) {
            if(nums[i]-j<=r) ans=(ans%m+rec(i+1,nums,j,nums[i]-j,dp1,m)%m)%m;
        }
        return dp1[i][l][r]=ans;
    }
    int countOfPairs(vector<int>& nums) {
        int m=1e9+7;
        int n=nums.size();
        vector<vector<vector<int>>> dp1(n+1,vector<vector<int>> (53,vector<int> (53,-1)));
        int ans=rec(0,nums,0,52,dp1,m);
        return ans;
    }
};
