class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n = len(nums)
        MOD = 10**9 + 7
        max_num = max(nums)

        @lru_cache(None)
        def dfs(index: int, prev1: int, prev2: int) -> int:
            if index == n:
                return 1

            count = 0
            for i in range(prev1, nums[index] + 1):
                j = nums[index] - i
                if prev2 >= j:
                    count += dfs(index + 1, i, j)

            return count % MOD

        return dfs(0, 0, max_num) % MOD
