class Solution {
public:
    int n;
    const int mod = 1e9 + 7;
    vector<vector<int>> dp;

    int solve(vector<int>& nums, int ind, int prev) {
        if (ind == n) return 1;  
        if (dp[ind][prev] != -1) return dp[ind][prev];  

        int res = 0;
        for (int j = prev; j <= nums[ind]; j++) {
            int k = nums[ind] - j;
            if (ind == 0 || k <= nums[ind - 1] - prev) {
                res = (res + solve(nums, ind + 1, j)) % mod;
            }
        }

        return dp[ind][prev] = res;  
    }

    int countOfPairs(vector<int>& nums) {
        n = nums.size();
        dp.assign(n, vector<int>(2001,-1));  
        return solve(nums, 0, 0);
    }
};
