 int mod= 1e9+7;
int dp[2001][52];
int n;

class Solution {
public:


    int rec(int level,int last1,vector<int>& nums){
        if(level==n) return 1;

        if(dp[level][last1]!=-1) return dp[level][last1];

        int ans=0;
        if(level==0){
            for(int i=0;i<=50;i++){
                int j= nums[level]-i;
                if(i>=0 && j>=0){
                     ans+= rec(level+1, i,nums);
                    ans= (ans%mod +  mod)%mod;

                }
            }
        }
        else{
            int last2= nums[level-1]-last1;
            for(int i=0;i<=50;i++){
                if(i>= last1 && nums[level]-i<=last2 && nums[level]-i>=0){
                    ans+= rec(level+1, i,nums);
                    ans= (ans%mod +  mod)%mod;
                }
            }
        }

        return dp[level][last1]=ans;
    }
    int countOfPairs(vector<int>& nums) {
        n=nums.size();
        for(int i=0;i<=n;i++){
            for(int j=0;j<=50;j++){
                dp[i][j]=-1;
            }
        }

        int ans= rec(0,0,nums);

        return ans;




    }
};
