class Solution {
public:
    int dp[20005][53];
    int mod=1e9+7;
    int rec(int index,int prev1,int prev2,vector<int> &nums,int n)
    {
        if(index==n)
        return 1;
        if(dp[index][prev1+1]!=-1)
        return dp[index][prev1+1];
        int cnt=0;
        if(prev1==-1)
        {
              for(int i=0;i<=nums[index];i++)
              {
                  cnt+=rec(index+1,i,nums[index]-i,nums,n);
                  cnt%=mod;
              }
        }
        else
        {
              for(int i=0;i<=nums[index];i++)
              {
                  if(i>=prev1&&(nums[index]-i)<=prev2)
                  cnt+=rec(index+1,i,nums[index]-i,nums,n);
                    cnt%=mod;
              }
        }
        return dp[index][prev1+1]=cnt%mod;
    }
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        memset(dp,-1,sizeof(dp));
        return rec(0,-1,-1,nums,n);
    }
};
