class Solution {
private:
    int dp[2002][55];
    int mod = 1e9 + 7;
    int rec(int ind, int prev, vector<int>& nums, int n) {
        if(ind == n) {
            return 1;
        }

        if(dp[ind][prev+1] != -1) return dp[ind][prev+1];

        long long res = 0;
        if(prev == -1) {
            for(int i = 0; i <= nums[ind]; i++) {
                res += rec(ind + 1, i, nums, n);
                res %= mod;
            }
        } else {
            int incPrev = prev;
            int decPrev = nums[ind - 1] - prev;

            for(int i = 0; i <= nums[ind]; i++) {
                int incCurr = i;
                int decCurr = nums[ind] - i;

                if(incPrev <= incCurr && decCurr <= decPrev) {
                    res += rec(ind + 1, i, nums, n);
                    res %= mod;
                }
            }
        }

        return dp[ind][prev+1] = res % mod;
    }
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        memset(dp, -1, sizeof(dp));
        return rec(0, -1, nums, n) % mod;
    }
};
