class Solution {
public:
    const int mod = 1000000007;
    int func(int i , int a , int b , vector<int> &nums , vector<vector<vector<int>>> &dp){
        if(i == nums.size()) return 1;
        if(dp[i][a][b] != -1) return dp[i][a][b];
        int ans = 0;
        for(int j = a ; j <= nums[i] ; j++){
            int x = nums[i] - j;
            if(x >= 0 && x <= b) ans = (0LL + ans + func(i + 1 , j , x , nums , dp)) % mod;
        }
        return dp[i][a][b] = ans % mod;
    }
