class Solution {
public:
const int MOD = 1e9 + 7;
vector<vector<vector<int>>> dp;
int gcd(int a, int b) {
        while (b != 0) {
            int temp = b;
            b = a % b;
            a = temp;
        }
        return a;
    }

    long long binaryExpo(long long base, long long exp, long long mod) {
        long long result = 1;
        base = base % mod;

        while (exp > 0) {
            if (exp % 2 == 1) {
                result = (result * base) % mod;
            }
            exp = exp >> 1;  // Divide exp by 2
            base = (base * base) % mod;
        }

        return result;
    }

    int f(int i, int prev1, int prev2, vector<int>& nums) {
        if (i == nums.size()) {
            return 1;
        }

        if (dp[i][prev1][prev2] != -1) {
            return dp[i][prev1][prev2];
        }

        int ans = 0;

        for (int curr1 = prev1; curr1 <= nums[i]; ++curr1) {
            int curr2 = nums[i] - curr1;
            if (curr2 <= prev2) {
                ans = (ans + f(i + 1, curr1, curr2, nums)) % MOD;
            }
        }

        dp[i][prev1][prev2] = ans;
        return ans;
    }

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        dp= vector<vector<vector<int>>>(n, vector<vector<int>>(51, vector<int>(51, -1)));
        return f(0, 0, nums[0], nums);
    }
};
