class Solution {

    int mod = (int)(1e9 + 7);

    public int countOfPairs(int[] A) {

        int n = A.length;




        long[][][] dp = new long[51][51][n + 5];



        for(int i = 0; i <= A[0]; i++){

            if(A[0] - i >= 0) dp[i][A[0] - i][0] = 1;

        }


        for(int k =1; k  < n; k++){
            for(int i = 0; i <= 50; i++){
                int j = A[k] - i;
                if(j >= 0){
                    for(int p = 0; p <= 50; p++){
                        int pj = A[k-1]- p;
                        if(pj >= 0 && i >= p && j <= pj){
                            dp[i][j][k] += dp[p][pj][k-1];

                            dp[i][j][k] %= mod;
                        }
                    }

                }


            }

        }

        long res = 0;
        for(int i = 0; i <= 50; i++){
            if(A[n-1] - i >= 0){
                res += dp[i][A[n-1] - i][n-1];

                res %= mod;
            }
        }


        return (int)res;

    }
}
