#define ll long long
ll dp[2001][51][51];
class Solution {
public:
    ll mod=1e9+7;
    ll f(ll idx,int prev1,ll prev2,vector<ll> &a){
        if(idx==a.size())
        return 1;

        if(dp[idx][prev1][prev2]!=-1)
        return dp[idx][prev1][prev2];

        ll ans=0;
        for(ll num1=prev1;num1<=a[idx];num1++){
            ll num2=a[idx]-num1;
            if(num2>=0 && num2<=prev2){
                ans+=(f(idx+1,num1,num2,a))%mod;
            }
        }
        return dp[idx][prev1][prev2]= ans%mod;
    }
    int countOfPairs(vector<int>& nums) {
        vector<ll> a;

        memset(dp,-1,sizeof(dp));

        for(auto i:nums)
        a.push_back(i);

        return f(0,0,50,a);
    }
};
