class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n = len(nums)
        # array of positive nums
        # find two arrays that added together equal nums[i]
        cache = {}
        def count(arr1_last_val, arr2_last_val, i):
            if i == n:
                return 1
            if (arr1_last_val, arr2_last_val, i) in cache:
                return cache[(arr1_last_val, arr2_last_val, i)]
            ans = 0
            for arr1_new_val in range(arr1_last_val, nums[i] + 1):
                arr2_new_val = nums[i] - arr1_new_val
                if arr2_new_val >= 0 and arr2_new_val <= arr2_last_val:
                    ans += count(arr1_new_val, arr2_new_val, i + 1)
            cache[(arr1_last_val, arr2_last_val, i)] = ans
            return ans
        ans = 0
        for arr1_start_value in range(0, nums[0] + 1):
            arr2_needed_value = nums[0] - arr1_start_value
            ans += count(arr1_start_value, arr2_needed_value, 1)
        return ans % ((10 ** 9) + 7)
