class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = int(1e9) + 7

        @cache
        def solve(idx, pre1, pre2):
            if idx == len(nums):
                return 1
            res = 0
            curr1 = pre1
            while curr1 <= nums[idx]:
                curr2 = nums[idx] - curr1
                if curr2 >= 0 and curr2 <= pre2:
                    res += solve(idx + 1, curr1, curr2)
                curr1 += 1
            return res % MOD

        return solve(0, 0, inf) % MOD
