typedef long long ll;

class Solution {
public:

    const ll mod = 1e9 + 7;
    ll n;
    ll memo[55][55][2005];
    bool visited[55][55][2005];

    ll solve(ll x, ll y, ll p, vector<int> &nums) {
        if (p == n) {
            return 1;
        }
        if (visited[x][y][p]) {
            return memo[x][y][p];
        }
        visited[x][y][p] = 1;
        ll sum = 0;
        for (int i = x; i <= nums[p]; i++) {
            if (nums[p] - i > y) {
                continue;
            }
            sum += solve(i, nums[p] - i, p + 1, nums);
            sum %= mod;
        }
        return memo[x][y][p] = sum;
    }

    int countOfPairs(vector<int>& nums) {
        n = nums.size();
        ll res = 0;
        for (int i = 0; i <= nums[0]; i++) {
            res += solve(i, nums[0] - i, 1, nums);
            res %= mod;
        }
        return (int) res;
    }
};
