class Solution {
public:
int mod=1e9+7;
vector<vector<vector<int>>> dp;
int help(int i,int prev1,int prev2,vector<int>& nums) {
    if(i==nums.size()) {
        return 1;
    }

    if(dp[i][prev1][prev2] != -1) {
        return dp[i][prev1][prev2];
    }

    int count=0;
    for(int x=prev1; x<=nums[i]; x++) {
        int y=nums[i]-x;
        if(y<=prev2) {
            count=(count+help(i+1,x,y,nums))%mod;
        }
    }
    return dp[i][prev1][prev2]=count;
}
