#define MOD 1000000007

class Solution {
public:
    int countOfPairs(vector<int>& nums) {
 int n = nums.size();
    vector<vector<int>> dp(n, vector<int>(101, 0)); // dp[i][j] where arr1[i] = j

    // Base case
    for (int j = 0; j <= nums[0]; ++j) {
        dp[0][j] = 1; // arr1[0] = j, arr2[0] = nums[0] - j
    }

    // DP transition
    for (int i = 1; i < n; ++i) {
        vector<int> new_dp(101, 0);
        for (int j = 0; j <= nums[i]; ++j) {
            for (int k = 0; k <= j; ++k) {
                int arr2_val = nums[i] - j;
                if (arr2_val <= nums[i-1] - k) {
                    new_dp[j] = (new_dp[j] + dp[i-1][k]) % MOD;
                }
            }
        }
        dp[i] = new_dp;
    }

    // Sum all valid dp[n-1][j] values
    int result = 0;
    for (int j = 0; j <= nums[n-1]; ++j) {
        result = (result + dp[n-1][j]) % MOD;
    }

    return result;
    }
};
