class Solution {
public:
const int mod=1000000007;
int dp[2000][51][51];
    int find(int i,int l1,int l2,vector<int>&nums){
        if(i==nums.size())return 1;
        if(dp[i][l1][l2]!=-1)return dp[i][l1][l2];
        int ans=0;
        for(int j=0;j<=nums[i];++j){
            if(j>=l1 && (nums[i]-j)<=l2){
                ans=(ans+find(i+1,j,nums[i]-j,nums)%mod)%mod;
            }
        }
        return dp[i][l1][l2]=ans;
    }
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        memset(dp,-1,sizeof(dp));
        int ans=find(0,0,50,nums)%mod;
        return ans%mod;
    }
};
