#define ll long long
class Solution {
public:

    vector<vector<vector<ll>>> dp;
    ll MOD = 1e9 + 7;
    vector<int> a;
    int cal(int idx, int mx,int mn)
    {
        if(idx == -1)
            return 1;
        if(dp[idx][mx][mn] != -1)
            return dp[idx][mx][mn];



        dp[idx][mx][mn] = 0;
        for(int j = mx; j >= 0; -- j)
        {
            if(a[idx] - j >= mn)
            {
                dp[idx][mx][mn] += cal(idx - 1, j, a[idx] - j);
                dp[idx][mx][mn] %= MOD;
            }
        }

        return dp[idx][mx][mn];
    }

    int countOfPairs(vector<int>& nums) 
    {
        ll n = nums.size();
        a = nums;
        dp = vector<vector<vector<ll>>>(n,vector<vector<ll>>(51,vector<ll>(51,-1)));
        return cal(n-1,50,0)%MOD;
    }
};
