class Solution:
    def countOfPairs(self, nums: List[int]) -> int:

        @functools.lru_cache(maxsize=None)
        def f(i, a):
            if i == 0:
                return 1
            ret = 0
            for j in range(0, min(0, nums[i - 1] - nums[i]) + a + 1):
                ret += f(i - 1, j)
            return ret % MOD

        MOD = 10 ** 9 + 7
        n = len(nums)
        return sum(f(n - 1, i) for i in range(0, nums[n - 1] + 1)) % MOD
