class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        l = len(nums)
        MOD = 10 ** 9 + 7

        @cache
        def dfs(index, a):
            if index == l:
                return 1

            res = 0
            val = nums[index]

            prev_a = a
            prev_b = nums[index - 1] - prev_a if index else float('inf')
            start = 0
            if index:
                start = (val + (prev_a - prev_b)) // 2

            for cand in range(start, val + 1):
                c, d = cand, val - cand

                if c >= prev_a and d <= prev_b:
                    res += dfs(index + 1, c)

            return res

        return dfs(0, 0) % MOD
