class Solution {
    int mod = 1e9 + 7;
    vector<vector<long>> dp;
    int solve(vector<int>& nums, int curr, int n, int last1, int last2) {
        if(curr == n) return 1;

        if(dp[curr][last1] != -1) return dp[curr][last1];

        int val = nums[curr];
        long ans = 0;
        for(int i = last1; i <= val; ++i) {
            int rem = val - i;
            if(rem <= last2) {
                ans = (ans + solve(nums, curr + 1, n, i, rem)) % mod;
            }
        }

        return dp[curr][last1] = ans;
    }
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        dp = vector<vector<long>>(n + 1, vector<long>(51, -1));
        return solve(nums, 0, n, 0, 51);
    }
};
