#define ll long long int
const int mod = 1e9 + 7;

class Solution {
public:
    ll dp[2005][51][51];

    ll cnt(int i, int p1, int p2, vector<int>& nums) {
        if(i == nums.size())
            return 1;

        if(dp[i][p1][p2] != -1)
            return dp[i][p1][p2];

        ll ans = 0;
        for(int k = 0; k <= nums[i]; k++) {
            if(k >= p1 && nums[i] - k <= p2) {
                ans = (ans + cnt(i + 1, k, nums[i] - k, nums)) % mod;
            }
        }

        return dp[i][p1][p2] = ans;
    }

    int countOfPairs(vector<int>& nums) {
        memset(dp, -1, sizeof(dp));
        return cnt(0, 0, 50, nums);
    }
};
