#define MOD 1000000007
class Solution {
   private:
    int backTrack(int idx, vector<int>& nums, int prevA, int prevB, vector<vector<vector<int>>>& dpArr) {
        int n = nums.size();
        if (idx >= n)
            return 1;

        // cout<<idx<<" "<<prevA<<" "<<prevB<<'\n';

        if (dpArr[idx][prevA][prevB] != -1)
            return dpArr[idx][prevA][prevB] % MOD;

        int ans = 0;

        for (int i = prevA; i <= 50; i++) {
            for (int j = prevB; j >= 0; j--) {
                if (i + j == nums[idx])
                    ans = (ans % MOD + backTrack(idx + 1, nums, i, j, dpArr) % MOD) % MOD;
            }
        }

        return (dpArr[idx][prevA][prevB] = ans % MOD);
    }

   public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<vector<int>>> dpArr(n, vector<vector<int>>(51, vector<int>(51, -1)));
        return backTrack(0, nums, 0, 50, dpArr);
    }
};
