class Solution:
  def countOfPairs(self, nums: List[int]) -> int:
    @cache
    def dfs(i, lo, hi):
      if i == n:
        return 1
      res = 0
      for l in range(max(lo, nums[i] - hi), nums[i] + 1):
        res = (res + dfs(i + 1, l, nums[i] - l)) % mod
      return res

    mod = 10 ** 9 + 7
    n = len(nums)
    return dfs(0, 0, 1001)
