class Solution {
public:

    int solve(vector<int>&nums, int i, int last1, int last2, vector<vector<vector<int>>>&dp){
        // cout<<i<<" "<<last1<<" "<<last2<<endl;
        int M = 1e9 + 7;
        if(i >= nums.size()) return 1;
        if(dp[i][last1][last2+1] != -1) return dp[i][last1][last2+1]%M;
        int sum = nums[i];
        int t1 = 0;
        for(int j=0;j<=sum;j++){
            if(j >= last1 && (last2 == -1 || sum - j <= last2)){
                t1 = (t1%M + solve(nums, i+1, j, sum-j, dp)%M)%M;
                // cout<<"t1->"<<t1<<endl;
            }
        }
        return dp[i][last1][last2+1] = t1%M;
    }

    int countOfPairs(vector<int>& nums) {
        int M = 1e9 + 7;
        vector<vector<vector<int>>> dp(nums.size(), vector<vector<int>>(51, vector<int>(55,-1)));
        return solve(nums, 0, 0, -1, dp)%M;
    }
};
