class Solution {
public:
    using ll = long long;
    const ll m = 1e9 + 7;
    ll dp[2000][51];
    int countOfPairs(vector<int>& nums) {
        const int n = nums.size();
        memset(dp, 0, sizeof(dp));
        for(int i = 0; i <= nums[0]; i++) {
            dp[0][i] = 1;
        }
        for(int i = 1; i < n; i++) {
            int shift = nums[i] - nums[i - 1];
            for(int sh = max(-shift, 0); sh <= 50; sh++) {
                for(int q = 0; q <= 50 && q + sh <= 50; q++) {
                    dp[i][q] += dp[i - 1][q + sh];
                    dp[i][q] %= m;
                }
            }
        }
        // for(int i = 0; i < n; i++) {
        //     for(int q = 0; q < 5; q++) {
        //         cout << dp[i][q] << ' ';
        //     } cout << endl;
        // }
        ll tot= 0 ;
        for(int i = 0; i <= 50; i++) {
            tot += dp[n - 1][i];
            tot %= m;
        }
        return tot;
    }
};
