#define FRR(i,a,b) for(int i=a;i>=b;i--)
#define fr(i,n) for(int i=0;i<n;i++)
#define frr(i,n) for(int i=n-1;~i;i--)
const int MOD=1000000007;
class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        int m = 0;
        for(auto& i:nums)m = max(m, i);
        vector<vector<int>> dp(n, vector<int>(55, -1));
        auto DFS = [&](auto&& dfs, int idx, int prev)->int{
            if(idx==n)return 1;
            int& res = dp[idx][prev];
            if(res!=-1)return res;
            res = 0;
            long long ret = 0;
            for(int i=prev;i<=nums[idx];i++) {
                for(int j=(idx?nums[idx-1]-prev:nums[idx]);j>=0;j--){
                    if(i+j==nums[idx]){
                        ret += dfs(dfs, idx+1, i);
                        ret %= MOD;
                    }
                }
            }
            res = int(ret);
            return res;
        };

        return DFS(DFS, 0, 0);
    }
};
