class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = 10**9 + 7

        @cache
        def dfs(i, bigger):
            if i >= len(nums):
                return 1

            prev_small = nums[i - 1] - bigger
            res = 0
            for x in range(bigger, nums[i] + 1):
                if nums[i] - x <= prev_small:
                    res += dfs(i + 1, x) % MOD

            return res % MOD

        res = 0 
        for x in range(nums[0] + 1):
            res += dfs(1, x) % MOD 
        return res % MOD
