class Solution {
public:
    int dp[2001][52][52];
    int mod=1e9+7;
    int f(int ind,int prev1,int prev2,vector<int>&nums){
        if(ind>=nums.size()) return 1;
        if(dp[ind][prev1][prev2]!=-1) return dp[ind][prev1][prev2];
        int ans=0;
        for(int i=0;i<=nums[ind];i++){
            if(prev1==51){
                ans=(ans+f(ind+1,i,nums[ind]-i,nums)%mod)%mod;
            }
            else if(i>=prev1 && nums[ind]-i<=prev2){
                ans=(ans+f(ind+1,i,nums[ind]-i,nums)%mod)%mod;
            }
        }
        return dp[ind][prev1][prev2]=ans%mod;
    }
    int countOfPairs(vector<int>& nums) {
        memset(dp,-1,sizeof(dp));
        int n=nums.size();
        return f(0,51,51,nums)%mod;
    }
};
