class Solution {
public:
    int mod = 1e9 + 7;
    int dp[2001][51][51];
    int solve(int i,int last1,int last2,vector<int> &nums){
        if(i == nums.size()){
            return 1;
        }
        if(dp[i][last1][last2] != -1){
            return dp[i][last1][last2];
        }
        int a = 0;
        for(int j=0;j<=nums[i];j++){
            if(last1 <= j && last2 >= nums[i]-j){
                a += solve(i+1,j,nums[i]-j,nums);
                a %= mod;
            }
        }
        return dp[i][last1][last2] = a%mod;
    }
    int countOfPairs(vector<int>& nums) {
        memset(dp,-1,sizeof(dp));
        return solve(0,0,50,nums);
    }
};
