using ll = long long int;
const int MOD = 1e9+7;
// ll dp[2001][51];
class Solution {
public:
    int fn(int at, int lst, vector<int>&a, vector<vector<int>>& dp) {
        // printf("%d %d\n", at, lst);
        if (at == (int)a.size()) return 1;
        if (dp[at][lst] != -1) return dp[at][lst];
        ll ans = 0;
        for (int v = lst; v <= a[at]; v++) {
            if (a[at]-v <= a[at-1]-lst) {
                ans += fn(at+1, v, a, dp);
                ans %= MOD;
            }
        }
        return dp[at][lst] = ans;
    }
    int countOfPairs(vector<int>& nums) {
        int n = (int)nums.size();
        vector<vector<int>> dp(n+1, vector<int>(51,-1));
        ll ans = 0;
        for (int i = 0; i <= nums[0]; i++) {
            ans += fn(1, i, nums, dp);
            ans %= MOD;
        }
        return ans;
    }
};
