#include <vector>
using namespace std;

const int MOD = 1e9 + 7;

class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<vector<int>>> dp(n + 1, vector<vector<int>>(51, vector<int>(51, -1)));
        return countPairs(nums, 0, 0, 50, dp);
    }

private:
    int countPairs(const vector<int>& nums, int index, int minVal, int maxVal, vector<vector<vector<int>>>& dp) {
        if (index == nums.size()) {
            return 1;  // Base case: valid pairing of all elements
        }

        if (dp[index][minVal][maxVal] != -1) {
            return dp[index][minVal][maxVal];  // Return cached result
        }

        int result = 0;
        for (int i = minVal; i <= nums[index]; ++i) {
            int j = nums[index] - i;
            if (j <= maxVal) {
                result = (result + countPairs(nums, index + 1, i, j, dp)) % MOD;
            }
        }

        dp[index][minVal][maxVal] = result;  // Cache the result
        return result;
    }
};
