const int mod = 1e9+7;
class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        using ll = long long;

        int n = nums.size();
        if(n == 1)return (nums[0]+1);
        vector<vector<ll>>dp(n, vector<ll>(51, -1));
        auto DP = [&](int i, int x, auto&&DP) -> ll{
            if(i == n)return 1;
            if(dp[i][x] != -1){
                return dp[i][x];
            }
            auto &res = dp[i][x];
            res = 0;
            for(int j = 0; j <= nums[i]; j++){
                int a = nums[i]-j, b = nums[i-1]-x;
                if((j >= x) && (a <= b)){
                    res += DP(i+1, j, DP);
                    res %= mod;
                }
            }
            return res;
        };
        ll ans = 0;
        for(int i = 0; i <= nums[0]; i++){
            ans += DP(1, i, DP);
            ans %= mod;
        }
        return ans;
    }
};
