class Solution {
    int[] nums;
    int n;
    HashMap<String, Long> map = new HashMap<>();
    long mod = (long)Math.pow(10, 9)+7;
    public int countOfPairs(int[] nums) {
        this.nums = nums;
        n = nums.length;
        long res = helper(0, 0, 51);
        res %= mod;
        return (int)res;
    }

    public long helper(int t, int pre1, int pre2){
        if(t==n) return 1L;

        if(map.get(t+"*"+pre1)!=null) return map.get(t+"*"+pre1);

        long res = 0L;

        for(int i = pre1; i<=nums[t]; i++){
            if(nums[t] - i <= pre2){
                res = (res + helper(t+1, i, nums[t]-i))%mod;

            } 
        }
        map.put(t+"*"+pre1, res);
        return res;


    }
}
