class Solution {
public:
    int mod=1e9+7;
    int f(int ind,int val1,int val2,vector<int>&arr,vector<vector<vector<int>>>&dp){
        int n=arr.size();
        if(ind>=n) return 1;
        if(dp[ind][val1][val2]!=-1) return dp[ind][val1][val2];
        int ans=0;
        for(int i=val1;i<=arr[ind];i++){
            int v1=i,v2=arr[ind]-v1;
            if(v1>=val1 and v2<=val2){
                ans=(ans+f(ind+1,v1,v2,arr,dp))%mod;
            }
        }
        return dp[ind][val1][val2]=ans;
    }
    int countOfPairs(vector<int>& arr) {
        int n=arr.size();
        vector<vector<vector<int>>>dp(n,vector<vector<int>>(51,vector<int>(51,-1)));
        int ans=f(0,0,50,arr,dp);
        return ans;
    }
};
