class Solution {
public:
    int mod = 1e9 + 7;
    long long dp[2004][52];
    long long calc(int idx,int prev,vector<int> &a)
    {
        long long ans = 0,n = a.size(),i,x1,x2,y1,y2;
        if(idx == n)
        {
            return 1;
        }
        if(dp[idx][prev] != -1)
        {
            return dp[idx][prev];
        }
        for(i=prev;i<=50;i++)
        {
            if(idx == 0)
            {
                if(i <= a[idx])
                {
                    ans+=calc(idx+1,i,a);
                    ans%=mod;
                }
            }
            else
            {
                x1 = prev;
                y1 = a[idx-1] - prev;
                x2 = i;
                y2 = a[idx] - i;
                if(x2 >=0 && y2 >= 0 && x1 <= x2 && y1 >= y2)
                {
                    ans+=calc(idx+1,i,a);
                    ans%=mod;
                }
            }
        }
        return dp[idx][prev] = ans;
    }
    int countOfPairs(vector<int>& nums) 
    {
        memset(dp,-1,sizeof(dp));
        return calc(0,0,nums);
    }
};
