class Solution {
public:
    int n;
    long M;
    long rec(int ind,long last1,long last2,vector<vector<vector<long>>>&dp,vector<int>&nums){
      if(ind==n){
          return 1;
      }  
      if(dp[ind][last1][last2]!=-1) return dp[ind][last1][last2];
      long ans=0;
        for(int i=last1;i<=nums[ind];i++){
            long cur=nums[ind]-i;
            if(last2>=cur&&cur>=0){
                ans+=rec(ind+1,i,cur,dp,nums);
                ans%=M;
            }
        }
        return dp[ind][last1][last2]=ans;
    } 
    int countOfPairs(vector<int>& nums) {
        n=nums.size();
        M=1e9+7;
        vector<vector<long>> t(55,vector<long>(55,-1));
        vector<vector<vector<long>>>dp(n,t);
        // return 0;
        return rec(0,0,51,dp,nums);
    }
};
