class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n = len(nums)
#         if n == 1:
#             return nums[0] + 1

        # arr1 = [0] * n
        # arr2 = nums.copy()

        MOD = 10 ** 9 + 7

        # for i in range(1,n):
        #     if arr2[i] > arr1[i-1]:
        #         t = arr2[i]-arr1[i-1]
        #         arr2[i] -= t
        #         arr1[i] += t

        @cache
        def dfs(i,n1,n2):
            if i >= n:
                return 1

            cn1,cn2 = 0, nums[i]
            ca = 0
            while cn2 >= 0:
                if cn1 >= n1 and cn2 <= n2:
                    ca += dfs(i+1,cn1,cn2)
                cn1 += 1
                cn2 -= 1
            return ca % MOD

        return dfs(0,0,inf)
