class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n = len(nums)
        mod = (10**9)+7
        @lru_cache(None)
        def solve(ind, prev1, prev2):
            if ind==n:
                return 1
            s = 0
            for i in range(nums[ind]+1):
                a,b = i, nums[ind] - i
                if prev1<=a and prev2>=b:
                    s+=solve(ind+1, a,b)
            return s%mod
        return solve(0,-1, sys.maxsize)
