#include <vector>
using namespace std;

class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        const int MOD = 1e9 + 7;
        int n = nums.size();
        vector<vector<int>> dp(n, vector<int>(51, 0));

        // Initialize for the first element
        for (int x = 0; x <= nums[0]; ++x) {
            dp[0][x] = 1;
        }

        // Fill dp table
        for (int i = 1; i < n; ++i) {
            for (int x = 0; x <= nums[i]; ++x) {
                dp[i][x] = 0;
                for (int y = 0; y <= x; ++y) {
                    if (y + (nums[i] - x) <= nums[i-1]) {
                        dp[i][x] = (dp[i][x] + dp[i-1][y]) % MOD;
                    }
                }
            }
        }

        // Sum up all valid end states
        int result = 0;
        for (int x = 0; x <= nums[n-1]; ++x) {
            result = (result + dp[n-1][x]) % MOD;
        }

        return result;
    }
};
