class Solution {
public:
    int mod = 1e9 + 7;
    int check(int ind,int prev1,int prev2,vector<int> &nums,vector<vector<vector<int>>> &dp){
        if(ind==nums.size()){
            return 1;
        }
        if(dp[ind][prev1][prev2]!=-1){
            return dp[ind][prev1][prev2];
        }
        int sum = 0;
        for(int i=0;i<=nums[ind];i++){
            if(i>=prev1 && nums[ind] - i<=prev2){
                sum = (sum + check(ind + 1,i,nums[ind] - i,nums,dp))%mod;
            }
        }
        return dp[ind][prev1][prev2] = sum;
    }

    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        vector<vector<vector<int>>> dp(n+1,vector<vector<int>> (52,vector<int> (52,-1)));
        return check(0,0,51,nums,dp);
    }
};
