mod = 10**9 + 7


class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n = len(nums)

        @cache
        def dfs(i: int, p: int) -> int:
            if i == n: return 1
            res = 0
            for j in range(p, nums[i] + 1):
                last = nums[i - 1] - p if i > 0 else inf
                if nums[i] - j > last: continue
                res += dfs(i + 1, j)
                res %= mod
            return res

        return dfs(0, 0)
