class Solution {
public:
    long  mod=1e9+7;
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        vector<vector<long>>dp(n,vector<long>(51,0));
        long x=nums[0];
        for (int i=0;i<=x;i++){
            dp[0][i]=1;
        }
        for (int i=1;i<n;i++){
            int prev=nums[i-1];
            int curr=nums[i];
            for (int j=0;j<=curr;j++){
                for (int k=0;k<=prev;k++){
                    if (j>=k && (curr-j)<=(prev-k)){
                        dp[i][j]=(dp[i][j]+dp[i-1][k])%mod;
                    }
                }
            }
        }
        int ans=0;
        int last=nums[n-1];
        for (int i=0;i<=last;i++){
            ans=(ans+dp[n-1][i])%mod;
        }
        return ans;
    }
};
