class Solution {
public:
    int dp[2001][52][52],n,mod = 1000000007;
    vector<int> arr;
    int rec(int i, int prev1, int prev2){
       if(i == n)
           return 1;
        if(dp[i][prev1][prev2] != -1)
            return dp[i][prev1][prev2]%mod;
        int c1 = 0;
        int x = arr[i];
        for(int j = 0;j <= x;j++){
            if(j >= prev1 && x - j <= prev2){
                c1 = (c1 + rec(i + 1, j, x - j) % mod) % mod;
            }
        }
        return dp[i][prev1][prev2] = c1 % mod;
    }
    int countOfPairs(vector<int>& arr) {
        this->arr = arr;
        n = arr.size();
        memset(dp, -1, sizeof dp);
        return rec(0, 0, 51)%mod;
    }
};
